function getValue() {
	return inconVal +" "+inconColorVals
}
function setValue(inconValues) {
	var incon = inconValues.substring(0,inconValues.lastIndexOf(" "));
	var inconColor = inconValues.substring(inconValues.lastIndexOf(" ")+1);
	iconClick('icontest',incon);
	iconColorClick(inconColor);
}
function iconClick(id, iconValue) {
	inconVal = iconValue;
	colorData(id);
	document.getElementById('view_cont').innerHTML = "<i style=\"font-size:50px;\" class='"
			+ iconValue + "'></i>";
}

function iconColorClick(inconColorVal) {
	inconColorVals = inconColorVal;
	document.getElementById('view_cont').innerHTML = "<i style=\"font-size:50px;\" class='"
			+ inconVal + " " + inconColorVal + "'></i>"
}

function incoData(id) {
	var htmls = "";
	for (var i = 0; i < iconJson.length; i++) {
		htmls += "<div style=\"width:40px;height:40px;border:1px solid #fff;float:left;margin:2px;\">"
				+ "<div style=\"width:100%;height:36px;text-align:center;\"><i class=\""
				+ iconJson[i].name
				+ "\" onclick=\"iconClick('"
				+ id
				+ "','"
				+ iconJson[i].name
				+ "')\" style=\"cursor:pointer;\"></i></div></div>";
	}
	document.getElementById(id).innerHTML = htmls;
	OriginalId = id;
}

function colorData(id) {
	var htmls = "";
	for (var i = 0; i < colorJson.length; i++) {
		htmls += "<div style=\"width:40px;height:40px;border:1px solid #fff;float:left;margin:2px;\">"
				+ "<div style=\"width:100%;height:36px;text-align:center;\"><div style=\"cursor:pointer;margin:auto;width:30px;height:20px;background-color:"
				+ colorJson[i].name
				+ ";\" onclick=\"iconColorClick('"
				+ colorJson[i].name + "')\"></div></div></div>";
	}
	document.getElementById(id).innerHTML = htmls;
}
